ATTRIBUTE.VALUE:
BEGIN TRAN BL_$$PARAM_01;

USE $$PARAM_07;	  

/* SENDO A PRIMEIRA CARGA DA TABELA */

		UPDATE GCS.$$PARAM_01
		SET IC_REGISTRO_VIGENTE = 1
		WHERE IC_REGISTRO_VIGENTE = 9
		AND (SELECT COUNT(*) FROM GCS.$$PARAM_01 WHERE IC_REGISTRO_VIGENTE <> 9) = 0;

/* EXCLUSÃO LÓGICA DAS OCORRENCIAS QUE NÃO EXISTEM MAIS NO SISTEMA DE ORIGEM */

USE $$PARAM_07;
	
	UPDATE GCS.$$PARAM_01 
	SET 
		IC_REGISTRO_VIGENTE   = 0, 
		DT_FIM_REGISTRO       = DATEADD(DAY, -1, CAST('$$PARAM_02' AS DATE)), 
		TS_ALTERACAO_REGISTRO = GETDATE()
	WHERE 
        IC_REGISTRO_VIGENTE = 1
        AND
	    HASHBYTES('MD5', $$PARAM_03) NOT IN
	   (
         SELECT HASHBYTES('MD5', $$PARAM_03)
	     FROM 	GCS.$$PARAM_01
         WHERE 	IC_REGISTRO_VIGENTE = 9 
       )
	   AND	
	  (
         SELECT COUNT(*) 
         FROM   GCS.$$PARAM_01 
         WHERE  IC_REGISTRO_VIGENTE = 9
      ) > 0;

/* EXCLUIR REGISTROS QUE NÃO SOFRERAM ALTERAÇÃO */

USE $$PARAM_07;

		DELETE 
			FROM GCS.$$PARAM_01
		WHERE 
			IC_REGISTRO_VIGENTE = 9 AND
			CAST(HASHBYTES('MD5', $$PARAM_03) AS VARCHAR)+NU_CHAVE_SIGCS
			IN 
			(
				SELECT 
					CAST(HASHBYTES('MD5', $$PARAM_03) AS VARCHAR)+NU_CHAVE_SIGCS
				FROM 
					GCS.$$PARAM_01 
				WHERE 
					IC_REGISTRO_VIGENTE = 1
			);

/* FECGAR A VERSAO DOS REGISTROS ALTERADOS */

USE $$PARAM_07;

	UPDATE GCS.$$PARAM_01 
	SET 
		IC_REGISTRO_VIGENTE = 0, 
		DT_FIM_REGISTRO = DATEADD(DAY, -1, CAST('$$PARAM_02' AS DATE)), 
		TS_ALTERACAO_REGISTRO = GETDATE()
    WHERE 
	    IC_REGISTRO_VIGENTE = 1 AND
	    HASHBYTES('MD5', $$PARAM_03) IN
	(SELECT 
	    HASHBYTES('MD5', $$PARAM_03)
	FROM 
		GCS.$$PARAM_01
    WHERE 
		IC_REGISTRO_VIGENTE = 9);

/* INCLUIR OS REGISTROS NOVOS */

USE $$PARAM_07;

exec GCSSP001_DESABILITA_INDICE $$PARAM_01, $$PARAM_04;

DECLARE @NUM BIGINT
SELECT @NUM = COALESCE(MAX($$PARAM_05), 0) FROM GCS.$$PARAM_01 WHERE IC_REGISTRO_VIGENTE <> 9

INSERT INTO
                          GCS.$$PARAM_01
              (
                          $$PARAM_06a
              )
SELECT
             $$PARAM_06b
FROM
		GCS.$$PARAM_01
WHERE
		IC_REGISTRO_VIGENTE = 9
;

/* EXCLUIR OS REGISTROS DE TRABALHO REMANESCENTES */

USE $$PARAM_07;

DELETE FROM 
			GCS.$$PARAM_01
WHERE 
			IC_REGISTRO_VIGENTE = 9;

exec GCSSP003_HABILITA_INDICE $$PARAM_01, $$PARAM_04;

COMMIT TRAN BL_$$PARAM_01;

/* CONTABILIZA O NÚMERO DE REGISTROS INSERIDOS A PRIMEIRA VEZ NA TABELA */
USE $$PARAM_07;
SELECT $$PARAM_05 FROM GCS.$$PARAM_01 WHERE IC_REGISTRO_VIGENTE = 1 AND CAST(DT_INICIO_REGISTRO AS DATE) = CAST('$$PARAM_02' AS DATE) AND HASHBYTES('MD5', $$PARAM_03) NOT IN ( SELECT HASHBYTES('MD5', $$PARAM_03) FROM GCS.$$PARAM_01 WHERE IC_REGISTRO_VIGENTE = 0 AND DT_FIM_REGISTRO = DATEADD(DAY, -1, CAST('$$PARAM_02' AS DATE)) )

USE $$PARAM_07;

DELETE FROM GCS.$$PARAM_01
WHERE IC_REGISTRO_VIGENTE = 9;

USE $$PARAM_07;

exec GCSSP003_HABILITA_INDICE $$PARAM_01, $$PARAM_04;

BULK INSERT $$PARAM_01 
FROM 'E:\upload\sigcs\$$PARAM_01' 
WITH (FIELDTERMINATOR=';', ROWTERMINATOR='0X0a', MAXERRORS = 0);
